// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

contract Crowdfunding {
    struct Campaign {
        address owner;
        string title;
        string description;
        uint256 target;
        uint256 deadline;
        uint256 amount_collected;
        string image;
        address[] donators;
        uint256[] donations;
    }

    mapping(uint256 => Campaign) public campaigns;
    uint256 public number_of_campaigns = 0;

    event CampaignCreated(uint256 campaignId, address owner, string title, uint256 target, uint256 deadline);
    event DonationReceived(uint256 campaignId, address donor, uint256 amount);
    event CampaignFunded(uint256 campaignId, uint256 totalCollected);

    modifier onlyOwner(uint256 _id) {
        require(msg.sender == campaigns[_id].owner, "Only the campaign owner can call this.");
        _;
    }

    modifier campaignExists(uint256 _id) {
        require(_id < number_of_campaigns, "Campaign does not exist.");
        _;
    }

    function createCampaign(address _owner, string memory _title, string memory _description, 
    uint256 _target, uint256 _deadline, string memory _image) public returns (uint256) {
        require(_deadline > block.timestamp, "The deadline must be in the future");
        Campaign storage campaign = campaigns[number_of_campaigns];
        campaign.owner = _owner;
        campaign.title = _title;
        campaign.description = _description;
        campaign.target = _target;
        campaign.deadline = _deadline;
        campaign.image = _image;
        campaign.amount_collected = 0;

        number_of_campaigns++;
        emit CampaignCreated(number_of_campaigns - 1, _owner, _title, _target, _deadline);
        return number_of_campaigns - 1;
    }

    function donateToCampaign(uint256 _id) public payable campaignExists(_id) {
        uint256 amount = msg.value;
        Campaign storage campaign = campaigns[_id];

        campaign.donators.push(msg.sender);
        campaign.donations.push(amount);

        (bool sent,) = payable(campaign.owner).call{value: amount}("");

        if (sent) {
            campaign.amount_collected += amount;
            emit DonationReceived(_id, msg.sender, amount);
            if (campaign.amount_collected >= campaign.target) {
                emit CampaignFunded(_id, campaign.amount_collected);
            }
        }
    }

    function getDonators(uint256 _id) public view campaignExists(_id) returns (address[] memory, uint256[] memory) {
        return (campaigns[_id].donators, campaigns[_id].donations);
    }

    function getCampaigns() external view returns (Campaign[] memory) {
        Campaign[] memory allCampaigns = new Campaign[](number_of_campaigns);
        for (uint256 i = 0; i < number_of_campaigns; i++) {
            allCampaigns[i] = campaigns[i];
        }
        return allCampaigns;
    }
}

contracts/TokenMarketplace_v2.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract TokenMarketplace {
    struct Listing {
        address seller;
        address tokenAddress;
        uint256 tokenAmount;
        uint256 totalPrice;
        bool active;
    }

    mapping(uint256 => Listing) public listings;
    uint256 public listingCounter;

    event ListingCreated(
        uint256 indexed listingId,
        address indexed seller,
        address indexed tokenAddress,
        uint256 tokenAmount,
        uint256 totalPrice
    );
    event ListingPurchased(
        uint256 indexed listingId,
        address indexed buyer,
        uint256 tokenAmount,
        uint256 totalPrice
    );

    function createListing(
        address _tokenAddress,
        uint256 _tokenAmount,
        uint256 _totalPrice
    ) external {
        require(_tokenAmount > 0, "Token amount must be greater than 0");
        require(_totalPrice > 0, "Total price must be greater than 0");

        IERC20 tokenContract = IERC20(_tokenAddress);
        require(
            tokenContract.balanceOf(msg.sender) >= _tokenAmount,
            "Insufficient token balance"
        );

        // Approve the TokenMarketplace contract to spend the listed token amount
        require(
            tokenContract.approve(address(this), _tokenAmount),
            "Approval failed"
        );

        listings[listingCounter] = Listing(
            msg.sender,
            _tokenAddress,
            _tokenAmount,
            _totalPrice,
            true
        );

        emit ListingCreated(
            listingCounter,
            msg.sender,
            _tokenAddress,
            _tokenAmount,
            _totalPrice
        );
        listingCounter++;
    }

    function purchaseListing(uint256 _listingId) external payable {
        Listing storage listing = listings[_listingId];
        require(listing.active, "Listing is not active");
        require(
            msg.value == listing.totalPrice,
            "Incorrect payment amount"
        );

        IERC20 tokenContract = IERC20(listing.tokenAddress);

        require(
            tokenContract.transferFrom(
                listing.seller,
                msg.sender,
                listing.tokenAmount
            ),
            "Token transfer failed"
        );

        payable(listing.seller).transfer(listing.totalPrice);
        listing.active = false;

        emit ListingPurchased(
            _listingId,
            msg.sender,
            listing.tokenAmount,
            listing.totalPrice
        );
    }

    function cancelListing(uint256 _listingId) external {
        Listing storage listing = listings[_listingId];
        require(
            listing.seller == msg.sender,
            "Only the seller can cancel the listing"
        );
        require(listing.active, "Listing is not active");

        listing.active = false;
    }
}

contracts/TokenMarketplace.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract TokenMarketplace is ReentrancyGuard {
    using SafeERC20 for ERC20;

    struct Offer {
        address seller;
        address token;
        uint256 tokenAmount;
        uint256 totalPrice; // Total price for all tokens in wei
        uint256 campaignId; // Unique ID of the campaign associated with these tokens
        bool isActive;
    }

    Offer[] public offers;

    event OfferCreated(uint256 indexed offerId, address indexed token, address indexed seller, uint256 tokenAmount, uint256 totalPrice, uint256 campaignId);
    event OfferCancelled(uint256 indexed offerId);
    event TokensPurchased(uint256 indexed offerId, address indexed token, address indexed buyer, uint256 tokenAmount, uint256 totalPrice);
    event DebugInfo(address indexed user, string message, uint256 value);

    function listTokens(address tokenAddress, uint256 tokenAmount, uint256 totalPrice, uint256 campaignId) public {
    emit DebugInfo(msg.sender, "Function Entry", 0);

    require(tokenAmount > 0, "Token amount must be greater than zero.");
    emit DebugInfo(msg.sender, "Checked tokenAmount > 0", tokenAmount);

    require(totalPrice > 0, "Total price must be greater than zero.");
    emit DebugInfo(msg.sender, "Checked totalPrice > 0", totalPrice);

    ERC20 token = ERC20(tokenAddress);
    emit DebugInfo(msg.sender, "ERC20 token initialized", 0);

    uint256 allowance = token.allowance(msg.sender, address(this));
    emit DebugInfo(msg.sender, "Token allowance checked", allowance);

    require(allowance >= tokenAmount, "Check the token allowance");
    emit DebugInfo(msg.sender, "Token allowance sufficient", allowance);

    token.safeTransferFrom(msg.sender, address(this), tokenAmount);
    emit DebugInfo(msg.sender, "Tokens transferred", tokenAmount);

    Offer memory newOffer = Offer({
        seller: msg.sender,
        token: tokenAddress,
        tokenAmount: tokenAmount,
        totalPrice: totalPrice,
        campaignId: campaignId,
        isActive: true
    });

    offers.push(newOffer);
    uint256 offerId = offers.length - 1;
    emit OfferCreated(offerId, tokenAddress, msg.sender, tokenAmount, totalPrice, campaignId);
    emit DebugInfo(msg.sender, "New offer created", offerId);
    }

    function cancelOffer(uint256 offerId) public {
        require(offerId < offers.length, "Offer does not exist.");
        Offer storage offer = offers[offerId];
        require(offer.seller == msg.sender, "Only the seller can cancel this offer.");
        require(offer.isActive, "Offer is not active.");

        offer.isActive = false;
        ERC20 token = ERC20(offer.token);
        token.safeTransfer(msg.sender, offer.tokenAmount);

        emit OfferCancelled(offerId);
    }

    function buyTokens(uint256 offerId) public payable nonReentrant {
        require(offerId < offers.length, "Offer does not exist.");
        Offer storage offer = offers[offerId];
        require(offer.isActive, "Offer is not active.");
        require(msg.value == offer.totalPrice, "Incorrect amount of ETH sent.");

        offer.isActive = false;
        payable(offer.seller).transfer(msg.value);
        ERC20 token = ERC20(offer.token);
        token.safeTransfer(msg.sender, offer.tokenAmount);

        emit TokensPurchased(offerId, offer.token, msg.sender, offer.tokenAmount, msg.value);
    }

    function getListings() public view returns (Offer[] memory) {
    uint256 activeCount = 0;

    // First, count active offers
    for (uint256 i = 0; i < offers.length; i++) {
        if (offers[i].isActive) {
            activeCount++;
        }
    }

    // Initialize an array to store active offers
    Offer[] memory activeOffers = new Offer[](activeCount);
    uint256 j = 0;

    // Populate the array with active offers
    for (uint256 i = 0; i < offers.length; i++) {
        if (offers[i].isActive) {
            activeOffers[j] = offers[i];
            j++;
        }
    }

    return activeOffers;
}

}

